import React,{Component} from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import {  withRouter } from 'react-router-dom';
import { withFirebase } from '../Firebase';
import * as ROUTES from '../../constants/routes';
import * as ROLES from '../../constants/roles';
const SignUpPage = () => (
  <div>
    <center>
    
    <br/>
    <div style={{textAlign:'center',padding:'2%',width:'85%'}}>
    <SignUpForm />
    </div>
    </center>
  </div>
);
const INITIAL_STATE = {
  username: '',
  email: '',
  passwordOne: '',
  passwordTwo: '',
  isAdmin: false,
  error: null,
};

class SignUpFormBase extends Component {
  
  constructor(props) {
    super(props);
    this.state = { ...INITIAL_STATE };
  }
  
  onSubmit = event => {
    const { username, email, passwordOne, isAdmin } = this.state;
    const roles = [];

    if (isAdmin) {
      roles.push(ROLES.ADMIN);
    }

    this.props.firebase
      .doCreateUserWithEmailAndPassword(email, passwordOne)
      .then(authUser => {
        // Create a user in your Firebase realtime database
        this.props.firebase
          .user(authUser.user.uid)
          .set({
            username,
            email,
            roles,
          })
          .then(()=>{
            return this.props.firebase.doSendEmailVerification();

          })
          .then(() => {
            this.setState({ ...INITIAL_STATE });
            this.props.history.push(ROUTES.HOME);
          })
          .catch(error => {
            this.setState({ error });
          });
      })
      .catch(error => {
        this.setState({ error });
      });

    event.preventDefault();
  };

  onChange = event => {
    this.setState({ [event.target.name]: event.target.value });
  };

  onChangeCheckbox = event => {
    this.setState({ [event.target.name]: event.target.checked });
  };
  render() {
    const {
      username,
      email,
      passwordOne,
      passwordTwo,
      isAdmin,
      error,
    } = this.state;
    

    const isInvalid =
      passwordOne !== passwordTwo ||
      passwordOne === '' ||
      email === '' ||
      username === '';
     
    return (
      <div>
      <Grid container component="main" className={this.props.root}/>
      <CssBaseline/>
      <Grid item xs={false} sm={4} md={7} className={this.props} />
      <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square/>
        <div className={this.props.paper}>
          <Avatar className={this.props.avatar}>
            <LockOutlinedIcon />
          </Avatar>
          <Typography component="h1" variant="h5">
            Sign Up
          </Typography>
        </div>
      <form onSubmit={this.onSubmit} className={this.props.form}>
        <TextField
          name="username"
          value={username}
          onChange={this.onChange}
          type="text"
          label="Full Name"
          variant="outlined"
          margin="normal"
          required
          fullWidth
          autoFocus
          autoComplete="Full Name"
        />
        <br/>
        <br/>
        <TextField
         
          value={email}
          onChange={this.onChange}
          type="email"
          name="email"
          variant="outlined"
              margin="normal"
              required
              fullWidth
              label="Email Address"
              autoComplete="email"
        />
        <br/>
        <br/>
        <TextField
          name="passwordOne"
          value={passwordOne}
          onChange={this.onChange}
          type="password"
          variant="outlined"
          margin="normal"
          required
          fullWidth
          label="Password"
              
              autoComplete="current-password"
        />
        <br/>
        <br/>
        <TextField
          name="passwordTwo"
          value={passwordTwo}
          onChange={this.onChange}
          type="password"
          variant="outlined"
          margin="normal"
              required
              fullWidth
              label="Confirm-Password"
              autoComplete="Confirm-password"
        />
        <br/>
        <br/>
        
          
          <FormControlLabel
            control={<Checkbox checked={isAdmin} name="isAdmin" onChange={this.onChangeCheckbox} color="primary" />}
            label="Are You Admin ??"
          />
        
        <br/>
        <br/>
        
        <Button
              type="submit"
              disabled={isInvalid} 
              fullWidth
              variant="contained"
              color="primary"
              className={this.props.submit}
            >
              Sign Up
            </Button>
        <br/>
        <br/>
        {error && <p>{error.message}</p>}
      </form>
      </div>
    );
  }
}

const SignUpForm = withRouter(withFirebase(SignUpFormBase));
export default SignUpPage;
export { SignUpForm };
